type FilterProps = {
  label: string;
  id: string;
};

type Props = {
  filters: FilterProps[];
  onFilterClick(id?: string): void;
  items: ListItemProps[];
  totalItemsCount: number;
  t(arg: string, obj?: { [key: string]: string | boolean | number }): string;
};
export const MultidisciplinarySubject = ({
  filters,
  onFilterClick,
  items,
  totalItemsCount,
  t,
}: Props) => {
  return (
    <StyledWrapper>
      <StyledBackground>
        <OneColumn wide>
          <Header>
            <LayoutItem layout="extend">
              <Heading>
                {t('frontpageMultidisciplinarySubject.heading')}
              </Heading>
              <InfoText>{t('frontpageMultidisciplinarySubject.text')}</InfoText>
            </LayoutItem>
            <Illustration />
          </Header>
        </OneColumn>
      </StyledBackground>
      <StyledLayoutWrapper>
        <OneColumn wide>
          <LayoutItem layout="extend">
            <NavigationBox
              items={filters}
              onClick={(
                e: React.MouseEvent<HTMLElement>,
                id: string | undefined,
              ) => onFilterClick(id)}
              isButtonElements
            />
            <List items={items} totalCount={totalItemsCount} />
          </LayoutItem>
        </OneColumn>
      </StyledLayoutWrapper>
    </StyledWrapper>
  );
};

export default injectT(MultidisciplinarySubject);
